<?php		$zerif_bigtitle_background = get_theme_mod('zerif_bigtitle_background',"none");		echo '		<style>			section.big-title{				filter: progid:DXImageTransform.Microsoft.AlphaImageLoader(src=\''.$zerif_bigtitle_background.'\',sizingMethod=\'scale\');				background-image: url("'.$zerif_bigtitle_background.'");							}		</style>';		echo '<section class="big-title">';		echo '<div class="container">';		$zerif_bigtitle_title = get_theme_mod('zerif_bigtitle_title',__('To add a title here please go to Customizer','zerif-lite'));		$zerif_bigtitle_text = get_theme_mod('zerif_bigtitle_text',__('To add a text here please go to Customizer','zerif-lite'));		if( !empty($zerif_bigtitle_title) ):			echo '<h1 class="intro-text">'.__($zerif_bigtitle_title,'zerif-lite').'</h1>';		endif;		if( !empty($zerif_bigtitle_text) ):			echo '<span class="intro-content">'.__($zerif_bigtitle_text,'zerif-lite').'</span>';		endif;		$zerif_bigtitle_bluebutton_label = get_theme_mod('zerif_bigtitle_bluebutton_label',__('First button','zerif-lite'));		$zerif_bigtitle_bluebutton_url = get_theme_mod('zerif_bigtitle_bluebutton_url','#');		$zerif_bigtitle_redbutton_label = get_theme_mod('zerif_bigtitle_redbutton_label',__('One button','zerif-lite'));		$zerif_bigtitle_redbutton_url = get_theme_mod('zerif_bigtitle_redbutton_url','#');		$zerif_bigtitle_greenbutton_label = get_theme_mod('zerif_bigtitle_greenbutton_label',__('Another button','zerif-lite'));		$zerif_bigtitle_greenbutton_url = get_theme_mod('zerif_bigtitle_greenbutton_url','#');		if( (!empty($zerif_bigtitle_redbutton_label) && !empty($zerif_bigtitle_redbutton_url)) ||		(!empty($zerif_bigtitle_greenbutton_label) && !empty($zerif_bigtitle_greenbutton_url))):			echo '<div class="buttons">';				if ( !empty($zerif_bigtitle_bluebutton_label) && !empty($zerif_bigtitle_bluebutton_url) ):					echo '<a href="'.$zerif_bigtitle_bluebutton_url.'" class="btn btn-primary custom-button blue-btn">'.__($zerif_bigtitle_bluebutton_label,'zerif-lite').'</a>';				endif;							echo '</div>';		endif;		echo '</div>';		echo '<div class="clear"></div>';		echo '</section>';?>